INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (168,NULL,23,1,0,'Question score',NULL,NULL,0,1,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (169,100,2,1,0,'Feedback','Guidance for assessing business opportunity','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The applicants have a very clear understanding of the business opportunity and the problems that must be overcome to enable successful exploitation. The project is well aligned with these needs.<tr><th scope="row">7-8</th><td>The applicants have a good idea of the potential market and opportunities. The needs of the customer are central to the project''s objectives.<tr><th scope="row">5-6</th><td>The business opportunity is plausible but not clearly expressed in terms of customer needs.<tr><th scope="row">3-4</th><td>The business opportunity is unrealistic or poorly defined. The customer''s true needs are not well understood and are not linked to the project''s objectives.<tr><th scope="row">1-2</th><td>There is little or no business drive to the project. The results are not relevant to the target customers or no customer interests are provided.</table>',1,1,'ASSESSMENT');
INSERT IGNORE INTO `form_input_validator` (`form_input_id`, `form_validator_id`) VALUES (168,2);
