INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (170,NULL,23,1,0,'Question score',NULL,NULL,0,2,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (171,100,2,1,0,'Feedback','Guidance for assessing market opportunity','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The market size and dynamics are quantified clearly and to sufficient resolution to be relevant to the project. The market is clearly well understood. The return on investment is clearly stated, quantified and realistic.<tr><th scope="row">7-8</th><td>The market size and dynamics are described with some quantification relevant to the project. Market understanding is acceptable and the return on investment is achievable.<tr><th scope="row">5-6</th><td>The market size and dynamics are understood but poorly quantified or stated at a level not really relevant for the project. Return on investment is plausible or badly defined.<tr><th scope="row">3-4</th><td>The market size is not quantified but there is some understanding. Return on investment is ill defined or unrealistic.<tr><th scope="row">1-2</th><td>The market is not well defined or is wrong. No sensible return on investment is provided.</table>',1,2,'ASSESSMENT');
INSERT IGNORE INTO `form_input_validator` (`form_input_id`, `form_validator_id`) VALUES (170,2);
