INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (173,100,2,1,0,'Feedback','Guidance for assessing project exploitation','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The principle exploitable outputs of the project are identified together with clear and achievable exploitation methods. Dissemination opportunities are also identified and appropriate.<tr><th scope="row">7-8</th><td>The main exploitable output of the project is identified and a realistic method defined. Some dissemination is also explained.<tr><th scope="row">5-6</th><td>An exploitation method is defined but lacking in detail or is only just feasible. Dissemination is mentioned.<tr><th scope="row">3-4</th><td>The exploitation and dissemination methods described are unrealistic or ill-defined.<tr><th scope="row">1-2</th><td>The exploitation method is missing or un-feasible and unlikely to succeed.</table>',1,3,'ASSESSMENT');
