INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (174,NULL,23,1,0,'Question score',NULL,NULL,0,4,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (175,100,2,1,0,'Feedback','Guidance for assessing project benefits','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>Inside and outside benefits are well defined, realistic and of significantly positive economic, environmental or social impact. Routes to exploit these benefits are also provided.<tr><th scope="row">7-8</th><td>Some positive outside benefits are defined and are realistic. Methods of addressing these opportunities are described.<tr><th scope="row">5-6</th><td>Some positive outside benefits are described but the methods to exploit these are not obvious. Or the project is likely to have a negative impact but some mitigation or a balance against the internal benefits is proposed.<tr><th scope="row">3-4</th><td>The project has no outside benefits or is potentially damaging to other stakeholders. No mitigation or exploitation is suggested.<tr><th scope="row">1-2</th><td>The project is damaging to other stakeholders with no realistic mitigation or balance described.</table>',1,4,'ASSESSMENT');
