INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (176,NULL,23,1,0,'Question score',NULL,NULL,0,5,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (177,100,2,1,0,'Feedback','Guidance for assessing technical approach','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The project plan is fully described and complete with milestones and timeframes. The plan is realistic and should meet the objectives of the project.<tr><th scope="row">7-8</th><td>The plan is well described and complete. There is a reasonable chance that it will meet the objectives of the project.<tr><th scope="row">5-6</th><td>The plan is not completely described or there may be deficiencies in some aspects. More work will be required before the plan can be said to be realistic.<tr><th scope="row">3-4</th><td>The plan has serious deficiencies or major missing aspects. The plan has little chance of meeting the objectives of the project.<tr><th scope="row">1-2</th><td>The plan is totally unrealistic or fails to meet the objectives of the project.</table>',1,5,'ASSESSMENT');
