INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (179,100,2,1,0,'Feedback','Guidance for assessing innovation','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The project is significantly innovative either commercially or technically and will make a substantial contribution to the field. Solid evidence is presented to substantiate the level of innovation.<tr><th scope="row">7-8</th><td>The project will be innovative and relevant to the market. There is high confidence that there is freedom to operate.<tr><th scope="row">5-6</th><td>The project is innovative but there is a lack of presented evidence as to the freedom to operate.<tr><th scope="row">3-4</th><td>The project lacks sufficient innovation both technically and commercially.<tr><th scope="row">1-2</th><td>The project is either not innovative or there is no exploitable route due to previous IP.</table>',1,6,'ASSESSMENT');
