INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (180,NULL,23,1,0,'Question score',NULL,NULL,0,7,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (181,100,2,1,0,'Feedback','Guidance for assessing risks','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>A thorough risk analysis has been presented across all 3 risk categories. The mitigation and risk management strategies proposed are also appropriate and professional.<tr><th scope="row">7-8</th><td>A good risk analysis has been carried out and the management methods and mitigation strategies proposed are realistic.<tr><th scope="row">5-6</th><td>Most major risks have been identified but there are some gaps or the mitigation and management is insufficient to properly control the risks.<tr><th scope="row">3-4</th><td>The risk analysis is poor or misses major areas of risk. The mitigation and management is poor.<tr><th scope="row">1-2</th><td>The risk analysis is superficial with minimal mitigation or management suggested.</table>',1,7,'ASSESSMENT');
