INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (187,NULL,23,1,0,'Question score',NULL,NULL,0,15,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (188,100,2,1,0,'Feedback','Guidance for assessing financial commitment','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The project costs are entirely appropriate. Any mix of research and development types (eg industrial research with some work packages of experimental development) is justified and costed correctly.<tr><th scope="row">7-8</th><td>The project costs are appropriate and should be sufficient to successfully complete the project. Any mix of research and development types is justified and costed correctly.<tr><th scope="row">5-6</th><td>The project costs and justifications are broadly correct but there are some concerns. Any mix of research and development types is just about acceptable.<tr><th scope="row">3-4</th><td>The costs are either too high or too low to carry out the project. Poor justification is provided for any research and development type mix.<tr><th scope="row">1-2</th><td>The costs are not appropriate or justified. Any mix of research and development type is not justified.</table>',1,15,'ASSESSMENT');
