INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (189,NULL,23,1,0,'Question score',NULL,NULL,0,16,'ASSESSMENT');
INSERT IGNORE INTO `form_input` (`id`, `word_count`, `form_input_type_id`, `competition_id`, `included_in_application_summary`, `description`, `guidance_question`, `guidance_answer`, `priority`, `question_id`, `scope`) VALUES (190,100,2,1,0,'Feedback','Guidance for assessing added value','<p>Your score should be based upon the following:<table><tr><th scope="row">9-10</th><td>The project will significantly increase the industrial partners'' R&D spend during the project and afterwards. The additionality arguments are very strong and believable.<tr><th scope="row">7-8</th><td>The project will increase the industrial partners'' commitment to R&D. The additionality arguments are good and justified.<tr><th scope="row">5-6</th><td>The project will improve the industrial partners'' commitment to R&D. The additionality arguments are just about acceptable.<tr><th scope="row">3-4</th><td>There is not likely to be any improvement to the industrial partner''s commitment to R&D. The additionality arguments are poor or not sufficiently justified.<tr><th scope="row">1-2</th><td>The work should be funded internally and does not deserve state funding.</table>',1,16,'ASSESSMENT');
