INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (3,NULL,(SELECT id FROM `section_template` where name = 'Project details' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),'Public description','Public description\n','Please provide a brief description of your project. If your application is successful, we will publish this description. This question is mandatory but we will not assess this content as part of your application.',NULL,NULL);
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (4,NULL,(SELECT id FROM `section_template` where name = 'Project details' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),'How does your project align with the scope of this competition?','Scope','If your application doesn\'t align with the scope, we will reject it.','Guidance for assessing scope','<p>Your answer should be based upon the following:<table><tr><th scope=\"row\">YES<td><p>The application contains the following:<ul><li>Is the consortia business led?<li>Are there two or more partners to the collaboration?<li>Does it meet the scope of the competition as defined in the competition brief?</ul><tr><th scope=\"row\">NO<td><p>One or more of the above requirements have not been satisfied.</table><p class=extra-margin>Your assessment of the project scope should be based upon the <a href=#>competition brief</a>.');
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (5,'9',(SELECT id FROM `section_template` where name = 'Application questions' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),'What will your project cost?','Funding','Tell us the total costs of the project and how much funding you need from Innovate UK. Please provide details of your expected project costs along with any supporting information. Please justify any large expenditure in your project.','Guidance for assessing financial commitment','<p>Your score should be based upon the following:<table><tr><th scope=\"row\" style=\"width: 50px\">9-10<td>The project costs are entirely appropriate. Any mix of research and development types (eg industrial research with some work packages of experimental development) is justified and costed correctly.<tr><th scope=\"row\" style=\"width: 50px\">7-8<td>The project costs are appropriate and should be sufficient to successfully complete the project. Any mix of research and development types is justified and costed correctly.<tr><th scope=\"row\" style=\"width: 50px\">5-6<td>The project costs and justifications are broadly correct but there are some concerns. Any mix of research and development types is just about acceptable.<tr><th scope=\"row\" style=\"width: 50px\">3-4<td>The costs are either too high or too low to carry out the project. Poor justification is provided for any research and development type mix.<tr><th scope=\"row\" style=\"width: 50px\">1-2<td>The costs are not appropriate or justified. Any mix of research and development type is not justified.</table>');
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (6,'10',(SELECT id FROM `section_template` where name = 'Application questions' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),'How does financial support from Innovate UK and its funding partners add value?','Adding value',' ','Guidance for assessing added value','<p>Your score should be based upon the following:<table><tr><th scope=\"row\" style=\"width: 50px\">9-10<td>The project will significantly increase the industrial partners\' R&D spend during the project and afterwards. The additionality arguments are very strong and believable.<tr><th scope=\"row\" style=\"width: 50px\">7-8<td>The project will increase the industrial partners\' commitment to R&D. The additionality arguments are good and justified.<tr><th scope=\"row\" style=\"width: 50px\">5-6<td>The project will improve the industrial partners\' commitment to R&D. The additionality arguments are just about acceptable.<tr><th scope=\"row\" style=\"width: 50px\">3-4<td>There is not likely to be any improvement to the industrial partner\'s commitment to R&D. The additionality arguments are poor or not sufficiently justified.<tr><th scope=\"row\" style=\"width: 50px\">1-2<td>The work should be funded internally and does not deserve state funding.</table>');
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (7,NULL,(SELECT id FROM `section_template` where name = 'Your finances' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),'Provide the project costs for \'{organisationName}\'',NULL,'Only your organisation can see this level of detail. All members of your organisation can access and edit your finances. We recommend assigning completion of your finances to one member of your team. <a href=\"https://www.gov.uk/government/publications/innovate-uk-completing-your-application-project-costs-guidance\" rel=\"external\">Find out which project costs are eligible.</a> ',NULL,NULL);
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (8,NULL,(SELECT id FROM `section_template` where name = 'Labour' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),NULL,NULL,'<p>You may claim the labour costs of all individuals you have working on your project.</p> <p> If your application is awarded funding, you will need to account for all your labour costs as they occur. For example, you should keep timesheets and payroll records. These should show the actual hours worked by individuals and paid by the organisation.</p>',NULL,NULL);
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (9,NULL,(SELECT id FROM `section_template` where name = 'Administration support costs' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),NULL,NULL,NULL,NULL,NULL);
INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (10,NULL,(SELECT id FROM `section_template` where name = 'Materials' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),NULL,NULL,'<p>You can claim the costs of materials used on your project providing:</p><ul class=\"list-bullet\"><li>they are not already purchased or included in the administration support costs</li><li>they are purchased from third parties</li><li>they won’t have a residual/resale value at the end of your project. If they do you can claim the costs minus this value</li></ul><p><a href=\"https://www.gov.uk/government/publications/innovate-uk-completing-your-application-project-costs-guidance\" rel=\"external\">Please refer to our guide to project costs for further information.</a></p>',NULL,NULL);
