INSERT INTO `question_template` (`priority`,`question_number`,`section_template_id`,`name`,`short_name`,`description`,`assessor_guidance_question`,`assessor_guidance_answer`) VALUES (10,NULL,(SELECT id FROM `section_template` where name = 'Subcontracting costs' and competition_template_id = (SELECT id from competition_template where competition_type_id = (SELECT id from competition_type WHERE NAME = 'Sector'))),NULL,NULL,'<p>You may subcontract work if you don’t have the expertise in your consortium. You can also subcontract if it is cheaper than developing your skills in-house.</p>',NULL,NULL);
