INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'What is the business need, technological challenge or market opportunity behind your innovation?','Need or challenge',5,1,@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'','What should I include in the need or challenge section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">the main motivation for the project; the business need, technological challenge or market opportunity</li><li class=\"li2\">the nearest current state-of-the-art (including those near-market/in development) and its limitations</li><li class=\"li2\">any work you have already done to respond to this need, for example is the project focused on developing an existing capability or building a new one?</li><li class=\"li2\">the wider economic, social, environmental, cultural and/or political challenges which are influential in creating the opportunity (for example, incoming regulations). Our Horizons tool can help here: http://www.slideshare.net/WebadminTSB/innovate-uk-horizons-sustainable-economy-framework</li></ul>',0,@q_need_or_challenge,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_need_or_challenge,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support the technical approach the project will undertake.</p><p>You may include, for example, a Gantt chart or project management structure.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 6 sides of A4. Longer appendices will only have the first 6 pages assessed</li><li>be less than 1mb in size</li></ul>',1,@q_need_or_challenge,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing need or challenge','Your score should be based upon the following:',0,@q_need_or_challenge,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_need_or_challenge,'1,2','There is little or no business drive to the project.  References to the current state-of-the-art are not offered or are not relevant.  ',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_need_or_challenge,'3,4','Project motivation is poorly defined or not relevant to the applicant or team.  References to the current state-of-the-art are not offered or are not relevant.  ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_need_or_challenge,'5,6','The project motivation is good but a lack of understanding of the nearest state-of-the-art or wider factors influencing this opportunity.  ',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_need_or_challenge,'7,8','There is a good motivation for the project. There is a good awarenessof the nearest state-of-the-art and wider factors influencing the opportunity.  ',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_need_or_challenge,'9,10','There is a compelling business motivation  for the project . There  is a clear understanding of the nearest state-of-the-art available.  The applicant has shown, if applicable, how the project will build on previous relevant work .   Any wider factors influencing this opportunity are identified. ',0);
