INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'What approach will you take and where will the focus of the innovation be?','Approach and innovation',6,'2',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'2. What is the size of the potential market for your project?','What should I include in the approach and innovation section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">how you will respond to the need, challenge or opportunity identified</li><li class=\"li2\">how you will improve on the nearest current state-of-the-art identified</li><li class=\"li2\">where the focus of the innovation will be in the project (application of existing technologies in new areas, development of new technologies for existing areas or a totally disruptive approach) and the freedom you have to operate</li><li class=\"li2\">how this project fits with your current product/service lines/offerings</li><li class=\"li2\">how it will make you more competitive</li><li class=\"li2\">the nature of the outputs you expect from the project (for example, report, demonstrator, know-how, new process, product or service design) and how these will help you to target the need, challenge or opportunity identified</li></ul>',0,@q_approach_and_innovation,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_approach_and_innovation,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>you may submit in pdf format up to 2 pages to provide graphics/diagrams/an image to explain the technology/product/service innovation.</p>',1,@q_approach_and_innovation,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing approach and innovation','Your score should be based upon the following:',0,@q_approach_and_innovation,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_approach_and_innovation,'1,2','The approach is not well defined or inconsistent with the need or challenge identified in Q1.  There is no identification of how this will improve competitiveness.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_approach_and_innovation,'3,4','The approach is poorly defined with an unconvincing link to the need or challenge identified in Q1. Improvement in competiveness and/or productivity is is not very convincing.',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_approach_and_innovation,'5,6','The project may address the need or challenge identified in Q1 and the innovations are highlighted. The level of innovation or freedom to operate is not strongly backed up with evidence.  The main risks are not fully identified . Innovation focus is plausible and shows a link to improvements in competitiveness and/or productivity.',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_approach_and_innovation,'7,8','The project addresses the need or challenge and the main innovations and risks are highlighted.  Evidence shows that the proposed development is innovative and that the applicant has the freedom to operate. It is demonstrated how the project outputs will differentiate from those of competitors.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_approach_and_innovation,'9,10','The approach addresses the need, challenge or opportunity identified in Q1. The main innovations and risks are identified. Evidence is presented to show how the innovation and project outputs will differentiate from those of competitors.  The project is significantly innovative either commercially or technically and will make a substantial contribution to the field.  Solid evidence is presented to substantiate the level of innovation and freedom to operate.',0);
