INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'Who is in the project team and what are their roles?','Team and resources',7,'3',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'3. How will you exploit and market your project?','What should I include in the team and resources section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">the roles, skills and relevant experience of all members of the project team in relation to the approach you will be taking</li><li class=\"li2\">the resources, equipment and facilities needed for the project and how you will access them</li><li class=\"li2\">details of any vital external parties, including sub-contractors, who you will need to work with to successfully carry out the project</li><li class=\"li2\">(if collaborative) the current relationships between project partners and how these will change as a result of the project</li><li class=\"li2\">highlight any gaps in the team that will need to be filled</li></ul>',0,@q_team_and_resources,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_team_and_resources,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>you may submit in pdf format up to 4 pages to describe the skills and experience of the main people who will be working on the project.</p>',1,@q_team_and_resources,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing team and resources','Your score should be based upon the following:',0,@q_team_and_resources,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_team_and_resources,'1,2','The applicant or consortium will not be capable of either carrying out the project or exploiting the results.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_team_and_resources,'3,4','There are significant gaps in the consortium with little or no information about how these will be filled.  There may be some partners with little relevance to the project activities. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_team_and_resources,'5,6','The applicant or consortium has most, but not all, of the required skills and experience required.  It is unclear whether or not the consortium will work well together ',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_team_and_resources,'7,8','The applicant or consortium makes sense given the approach described in Q2.  The applicant indicates how access will be obtained to all the resources, equipment and facilities they will need. The consortium is likely to work well.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_team_and_resources,'9,10','The applicant or consortium is well placed to carry out the project and exploit the results.  There is a clear plan  to obtain all the resources, equipment and facilities they will need.  There is strong evidence that the consortium will work well.',0);
