INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'What does the market you are targeting look like?',' Market awareness',8,'4',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'','What should I include in the market awareness section? ','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">the markets (domestic and/or international) you will be targeting in the project and any other potential markets</li><li class=\"li2\">for the target markets, describe:</li><ul><li class=\"li2\">the size of the target markets for the project outcomes, backed up by references where available</li><li class=\"li2\">the structure and dynamics of the market (such as customer segmentation), together with predicted growth rates within clear timeframes</li><li class=\"li2\">the main supply/value chains and business models in operation (and any barriers to entry)</li><li class=\"li2\">the current UK position in targeting this market</li></ul><li class=\"li2\">for highly innovative projects, where the market may be unexplored, explain:</li><ul><li class=\"li2\">what the route to market could or might be</li><li class=\"li2\">what its size might be</li><li class=\"li2\">how the project will look to explore the market potential</li></ul><li class=\"li2\">briefly describe the size and main features of any other markets not already listed</li></ul>',0,@q_market_awareness,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_market_awareness,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support the technical approach the project will undertake.</p><p>You may include, for example, a Gantt chart or project management structure.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 6 sides of A4. Longer appendices will only have the first 6 pages assessed</li><li>be less than 1mb in size</li></ul>',1,@q_market_awareness,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing market awareness','Your score should be based upon the following:',0,@q_market_awareness,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_market_awareness,'1,2','The market is poorly defined or is irrelevant to the motivations of the project.  ',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_market_awareness,'3,4','Some information about the general market is offered but the extent of the addressable market for the project is not described.  Secondary markets are barely mentioned. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_market_awareness,'5,6','The general market size and dynamics are understood but the addressable market is poorly quantified.  Secondary markets are mentioned but little information is offered. ',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_market_awareness,'7,8','There is a T good awareness of the target market’s drivers and dynamics. The market size is quantified with some evidence. For a new market, a good attempt is made at describing the possible routes to market and estimating the market size.  Relevant secondary markets are described showing good awareness.  ',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_market_awareness,'9,10','The target market size, drivers and dynamics are fully quantified and evidenced. Where the market is new or unexplored, possible routes are identified based on precedents. Relevant secondary markets are substantiated and described in brief. ',0);
