INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'How are you going to grow your business and increase your productivity into the long term as a result of the project?','Outcomes and route to market',9,'5',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'5. What technical approach will you use and how will you manage your project?','What should I include in the outcomes and route to market section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">your current position in the markets and supply/value chains outlined (will you be extending or establishing your market position?)</li><li class=\"li2\">your target customers and/or end users, and the value to them (why would they use/buy it?)</li><li class=\"li2\">your route to market</li><li class=\"li2\">how you are going to profit from the innovation (increased revenues or cost reduction)</li><li class=\"li2\">how the innovation will impact your productivity and growth (in the short and long-term)</li><li class=\"li2\">how you will protect and exploit the outputs of the project, for example through know-how, patenting, designs, changes to business model</li><li class=\"li2\">your strategy for targeting the other markets identified during or after the project</li><li class=\"li2\">for any research organisation activity in the project, describe your plans to spread project research outputs over a reasonable timescale</li><li class=\"li2\">if you expect to use the results generated from the project in further research activities, describe how</li></ul>',0,@q_outcomes,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support the technical approach the project will undertake.</p><p>You may include, for example, a Gantt chart or project management structure.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 6 sides of A4. Longer appendices will only have the first 6 pages assessed</li><li>be less than 1mb in size</li></ul>',0,@q_outcomes,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_outcomes,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing outcomes and route to market','Your score should be based upon the following:',0,@q_outcomes,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_outcomes,'1,2','The applicant provides little or no information about the target customers or projected growth.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_outcomes,'3,4','There is some information about the target customer types but there is little about the value proposition or how profit, productivity or growth will be affected.  The Projected Growth table is incomplete. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_outcomes,'5,6','Target customer types are described but the value proposition to them is less clear.  There is some information about how profit, productivity or growth increases may be achieved at some point.  The Projected Growth table is completed but there is little evidence to justify the projections.',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_outcomes,'7,8','Target customers are identified along with the value proposition to them.  The routes to market and how profit, productivity and growth will increase is outlined with some evidence. The exploitation and/or dissemination of the main project outputs is outlined. The Projected Growth table is completed and projections look sound.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_outcomes,'9,10','Target customers are identified along withthe value proposition  to them.  The routes to market and how profit, productivity and growth will increaseis identified and evidenced. .  The exploitation and/or dissemination of the main project outputs is outlined.The Projected Growth table is completed and projections look realistic.',0);
