INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'What impact might this project have outside the project team?','Wider impacts',10,'6',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'','What should I include in the wider impacts section?','<p class=\"p1\">You should describe and where possible measure:</p><ul><li class=\"li2\">the economic benefits from the project to external parties (customers, others in the supply chain, broader industry and the UK economy) such as productivity increases and import substitution</li><li class=\"li2\">any expected social impacts, either positive or negative on, for example:</li><ul><li class=\"li2\">quality of life</li><li class=\"li2\">social inclusion/exclusion</li><li class=\"li2\">jobs (safeguarded, created, changed, displaced)</li><li class=\"li2\">education</li><li class=\"li2\">public empowerment</li><li class=\"li2\">health and safety</li><li class=\"li2\">regulations</li><li class=\"li2\">diversity</li></ul><li class=\"li2\">any expected impact on government priorities</li><li class=\"li2\">any expected environmental impacts, either positive or negative</li><li class=\"li2\">identify any expected regional impacts of the project</li></ul>',0,@q_wider_impact,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support your response to this question. This appendix may include graphics describing the innovation or the nature of the problem. You may want to include evidence of freedom to operate, patent searches or competitor analysis as supporting information.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 5 sides of A4. Longer appendices will only have the first 5 pages assessed</li><li>be less than 1mb in size</li></ul>',0,@q_wider_impact,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_wider_impact,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing wider impacts','Your score should be based upon the following:',0,@q_wider_impact,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_wider_impact,'1,2','There is no information about how the project might impact others or the project would be detrimental to other UK interests.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_wider_impact,'3,4','The applicant provides some information about possible impacts but significant gaps remain. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_wider_impact,'5,6','There  is basic awareness of how the project could impact some others outside the project. Some relevant stakeholders are not considered.  Little mitigation is offered where there may be negative impacts.  ',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_wider_impact,'7,8','There is good awareness of how the project may impact others outside of the team. .  Expected regional impacts are described.  Any possible negative impacts are partially mitigated where appropriate.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_wider_impact,'9,10','The positive impact on others outside of the team is understood (supply chain partners, customers, broader industry, etc) Social, economic and/or environmental impacts are considered.  Expected regional impacts are described with compelling evidence to justify claims.  Any possible negative impacts are fully mitigated where appropriate. ',0);
