INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'How will you manage the project effectively?','Project management',11,'7',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'7. What are the risks (technical, commercial and environmental) to your project\'s success? What is your risk management strategy?','What should I include in the project management section?','<p>You should describe or explain:</p>\r\n<ul>\r\n <li class=\"li2\">the main work packages of the project, indicating the relevant research category, the lead partner assigned to each and the total cost of each one</li>\r\n <li class=\"li2\">your approach to project management, identifying any major tools and mechanisms that will be used for a successful and innovative project outcome.</li>\r\n <li class=\"li2\">the management reporting lines</li>\r\n <li class=\"li2\">your project plan in enough detail to identify any links or dependencies between work packages or milestones</li>\r\n</ul>',0,@q_project_management,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_project_management,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>you can submit a project plan/Gantt chart up to 2 A4 pages in length in pdf format</p>',1,@q_project_management,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for project management','Your score should be based upon the following:',0,@q_project_management,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_project_management,'1,2','The plan is totally unrealistic or fails to meet the objectives of the project.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_project_management,'3,4','The plan has serious deficiencies or major missing aspects. The plan has little chance of meeting the objectives of the project. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_project_management,'5,6','The project work packages are outlined but there are some details missing.  The plan seems reasonable but not tailored to the objectives of the project. ',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_project_management,'7,8','The project work packages are outlined with the research category, lead partner and total cost provided for each one.  The approach to project management is stated.  The plan seems appropriate to the project objectives.  Any links or dependencies between work packages or milestones are identified.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_project_management,'9,10','The project work packages are outlined with the research category, lead partner and total cost provided for each one.  The approach to project management is described.  The plan is designed to meet the objectives of the project in a realistic and efficient way.  Any links or dependencies between work packages or milestones are identified. ',0);
