INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'What are the main risks for this project?','Risks',12,'8',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'8. Does your project team have the skills, experience and facilities to deliver this project?','What should I include in the risks section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">the main risks and uncertainties of the project, including the technical, commercial, managerial and environmental risks, (providing a risk register if appropriate)</li><li class=\"li2\">how will these risks be mitigated</li><li class=\"li2\">any project inputs that are critical to completion (such as resources, expertise, data sets)</li><li class=\"li2\">any output likely to be subject to regulatory requirements, certification, ethical issues, etc, and how will you manage this?</li></ul>',0,@q_risks,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>you can submit a risk register of up to 2 A4 pages in length in pdf format</p>',0,@q_risks,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_risks,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing risks','Your score should be based upon the following:',0,@q_risks,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_risks,'1,2','The risk analysis is superficial with minimal mitigation or management suggested.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_risks,'3,4','The risk analysis is poor or misses major areas of risk. The mitigation and management is poor.',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_risks,'5,6','Most major risks have been identified but there are some gaps or the mitigation and management is insufficient to properly control the risks.',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_risks,'7,8','The key risks and uncertainties of the project are considered with appropriate mitigations. Relevant constraints or conditions on the project outputs are identified.  ',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_risks,'9,10','The key risks and uncertainties of the project are considered and mitigated.  Critical inputs to the project are identified.  Relevant constraints or conditions on the project outputs (regulatory requirements, certification or ethical issues) are identified. The risk analysis is appropriate and professional.',0);
INSERT INTO `form_input_validator` (form_input_id, form_validator_id) VALUES(@main_risks, @not_empty_validator);
