INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,'',1,0,'Describe the impact that an injection of public funding would have on this project.','Additionality',13,'9',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'9. What will your project cost?','What should I include in the additionality section?','<p class=\"p1\">You should describe or explain:</p><ul><li class=\"li2\">if this project could go ahead in any form without public funding and if so, the difference the public funding would make (such as faster to market, more partners and reduced risk)</li><li class=\"li2\">the likely impact of the project on the business of the partners involved</li><li class=\"li2\">why you are not able to wholly fund the project from your own resources or other forms of private-sector funding (what would happen if the application is unsuccessful)</li><li class=\"li2\">how this project would change the nature of R&amp;D activity the partners would undertake (and related spend)</li></ul>',0,@q_additionality,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_additionality,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support the technical approach the project will undertake.</p><p>You may include, for example, a Gantt chart or project management structure.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 6 sides of A4. Longer appendices will only have the first 6 pages assessed</li><li>be less than 1mb in size</li></ul>',1,@q_additionality,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing additionality','Your score should be based upon the following:',0,@q_additionality,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_additionality,'1,2','There is no justification for public funding and no reason why the applicant should not fund the work. ',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_additionality,'3,4','The funding arguments are poor or not sufficiently justified. There is not likely to be any improvement to the industrial partner’s commitment to R&D. ',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_additionality,'5,6','The public funding arguments are acceptable but the difference made by the grant will be modest. The project will improve the industrial partners’ commitment to R&D.',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_additionality,'7,8','The arguments for public funding are good and justified. The project will significantly increase the industrial partners’ commitment to R&D.',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_additionality,'9,10','There is a compelling case for the positive difference funding will make. Alternative sources of support are described with an explanation of why they are discounted or used in conjunction with the grant funding.  The project will significantly increase the industrial partners’ R&D spend during the project and afterwards.',0);
