INSERT INTO `question` (`assign_enabled`,`description`,`mark_as_completed_enabled`,`multiple_statuses`,`name`,`short_name`,`priority`,`question_number`,`competition_id`,`section_id`,`assessor_maximum_score`,`question_type`) VALUES (1,' ',1,0,'How much will the project cost and how does it represent value for money for the team and the taxpayer?','Costs and value for money',14,'10',@sector_template_id,@s_application_questions,10,'GENERAL');
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (400,2,@sector_template_id,1,'10. How does financial support from Innovate UK and its funding partners add value?','What should I include in the costs and value for money section?','<p>You should describe or explain:</p>\r\n<ul>\r\n <li class=\"li2\">the total project cost and the grant being requested in terms of the project goals</li>\r\n <li class=\"li2\">how the partners will finance their contributions to the project</li>\r\n <li class=\"li2\">how this project represents value for money for you and the taxpayer and how it compares to what you would spend your money on otherwise?</li>\r\n <li class=\"li2\">the balance of costs and grant across the project partners</li>\r\n <li class=\"li2\">any sub-contractor costs and why they are critical to the project</li>\r\n</ul>',0,@q_costs_and_value,'APPLICATION',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,23,@sector_template_id,0,'Question score',NULL,NULL,0,@q_costs_and_value,'ASSESSMENT',1);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (NULL,4,@sector_template_id,1,'Appendix','What should I include in the appendix?','<p>You may include an appendix of additional information to support the technical approach the project will undertake.</p><p>You may include, for example, a Gantt chart or project management structure.</p><p>The appendix should:</p><ul class=\"list-bullet\"><li>be in a portable document format (.pdf)</li><li>be readable with 100% magnification</li><li>contain your application number and project title at the top</li><li>not be any longer than 6 sides of A4. Longer appendices will only have the first 6 pages assessed</li><li>be less than 1mb in size</li></ul>',1,@q_costs_and_value,'APPLICATION',0);
INSERT INTO `form_input` (`word_count`,`form_input_type_id`,`competition_id`,`included_in_application_summary`,`description`,`guidance_title`,`guidance_answer`,`priority`,`question_id`,`scope`,`active`) VALUES (100,2,@sector_template_id,0,'Feedback','Guidance for assessing costs and value for money','Your score should be based upon the following:',0,@q_costs_and_value,'ASSESSMENT',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_costs_and_value,'1,2','The costs are not appropriate or justified. The balance between partners and subcontractors is not justified.',4);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_costs_and_value,'3,4','The project costs seem too high or too low given the proposed project. The split of costs and grants between partners is unbalanced, or inappropriate use is being made of subcontractors.',3);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_costs_and_value,'5,6','The project costs seem ok but the justifications are not clear. The balance of costs and grants between partners is acceptable .  Little information is offered about alternative approaches and the value for money this project offers.',2);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_costs_and_value,'7,8','The project costs are appropriate and should be sufficient to successfully complete the project. The balance of costs and grants between partners, and use of subcontractors seems reasonable The project represents good value for money compared to alternative outlined approaches (including doing nothing).',1);
INSERT INTO `guidance_row` (`form_input_id`,`subject`,`justification`,`priority`) VALUES (@f_costs_and_value,'9,10','The project costs are entirely appropriate and represent excellent value for money compared to alternative approaches outlined (including doing nothing)..  The partners have a clear idea of how they will finance their contribution.  The balance of costs and grants between partners, and use of subcontractors is justified and reasonable for the proposed project.',0);
