INSERT INTO `question` VALUES (111,'',NULL,'\0','\0','FINANCE_SUMMARY_INDICATOR_STRING',NULL,17,NULL,3,48,NULL,'GENERAL');
INSERT INTO `question` VALUES (112,'\0','<h2 class=\"heading-medium\">Funding rules for this competition</h2><p>We will fund projects between &pound;500,000 and &pound;1.5 million. We may consider projects with costs outside of this range. We expect projects to last between 12 and 36 months.</p><p>Innovate UK\'s aim is to optimise the level of funding businesses receive. We also recognise the importance of research organisations\' contribution to R&amp;D projects. Therefore we require the following levels of participation:</p><ul class=\"list-bullet\"> <li>at least 70% of the total eligible project costs are incurred by commercial organisations and</li> <li>a maximum of 30% of total eligible project costs are available to research participants. Where there is more than one research participant, this maximum will be shared between them.</li></ul>','\0','\0',NULL,NULL,16,NULL,3,48,NULL,'GENERAL');
INSERT INTO `question` VALUES (113,'\0','Enter the full title of the project','','\0','Application details','Application details',1,NULL,4,49,NULL,'GENERAL');
INSERT INTO `question` VALUES (114,'','Please provide a short summary of your project. We will not score this summary.','','\0','Project summary','Project summary',2,NULL,4,49,NULL,'GENERAL');
INSERT INTO `question` VALUES (115,'','Please provide a brief description of your project. If your application is successful, we will publish this description. This question is mandatory but is not scored.','','\0','Public description','Public description',3,NULL,4,49,NULL,'GENERAL');
