INSERT INTO `question` VALUES (127,'\0','Only your organisation can see this level of detail. All members of your organisation can access and edit your finances. We recommend assigning completion of your finances to one member of your team. <a href=\"https://www.gov.uk/government/publications/innovate-uk-completing-your-application-project-costs-guidance\" rel=\"external\">Find out which project costs are eligible.</a> ','','','Provide the project costs for \'{organisationName}\'','Project finances',15,NULL,4,53,NULL,'GENERAL');
